import React, { useState } from 'react';
import Questionario from './components/Questionario';

function App() {
  // Controle de qual tela mostrar: 1 (Perguntas), 2 (Chaves), 3 (Tabela)
  const [etapa, setEtapa] = useState(1);
  
  // Estado para guardar o que a IA gerou e o ID do banco SQL
  const [dadosBusca, setDadosBusca] = useState({
    id_busca: null,
    string_busca: '',
    contexto: ''
  });

  // Função chamada após o Questionário ser enviado com sucesso
  const prosseguirParaChaves = (resultadoIA, respostasOriginais) => {
    setDadosBusca({
      id_busca: resultadoIA.id_busca,
      string_busca: resultadoIA.string_busca,
      contexto: resultadoIA.contexto
    });
    setEtapa(2); // Muda para a Tela de Edição de Chaves
  };

  return (
    <div className="App">
      <header>
        <h1>SaaS de Busca Científica</h1>
      </header>

      <main>
        {etapa === 1 && (
          <Questionario aoFinalizar={prosseguirParaChaves} />
        )}

        {etapa === 2 && (
          <div>
            <h2>Tela 2: Revisão de Chaves</h2>
            <p>ID da Busca: {dadosBusca.id_busca}</p>
            <textarea 
              value={dadosBusca.string_busca} 
              onChange={(e) => setDadosBusca({...dadosBusca, string_busca: e.target.value})}
            />
            <button onClick={() => setEtapa(3)}>Buscar Artigos</button>
          </div>
        )}

        {etapa === 3 && (
          <div>
            <h2>Tela 3: Tabela Resumida</h2>
            <p>Aqui aparecerão os artigos filtrados pela IA...</p>
            <button onClick={() => setEtapa(1)}>Nova Pesquisa</button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
